<script type="text/javascript">






  
document.addEventListener("DOMContentLoaded", () => {
  const fadeElements = document.querySelectorAll('.fade-on-scroll');

  const observerOptions = {
    threshold: 0.5, // Trigger when 50% of the element is visible
    rootMargin: "0px 0px -100px 0px" // Adds extra bottom margin for smoother visibility detection
  };

  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      if (!entry.isIntersecting) {
        entry.target.classList.add('fade-out'); // Fade out when scrolled out
      } else {
        entry.target.classList.remove('fade-out'); // Fade back in when visible
      }
    });
  }, observerOptions);

  fadeElements.forEach(element => observer.observe(element));
});



document.addEventListener('DOMContentLoaded', function() {
    // Cart data stored in sessionStorage
    let cart = JSON.parse(sessionStorage.getItem('cart')) || [];
        new UomoSections.CartDrawer(); // Initialize the cart drawer on page load


    // Function to update the cart drawer UI
  function updateCartDrawer() {
    const cartItemsContainer = document.querySelector('#cart-items');
    const cartSubtotalElement = document.querySelector('.cart-subtotal');
    const cartItemsCountElement = document.querySelector('.js-cart-items-count');
    let subtotal = 0;
    let totalItems = 0;
    let itemsHTML = '';

    cart.forEach((item, index) => {
        subtotal += item.price * item.quantity;
        totalItems += item.quantity;
        itemsHTML += `
            <div class="cart-item d-flex justify-content-between mb-2 align-items-center" data-index="${index}">
                <div>
                    <p class="mb-0"><strong>${item.productName}</strong></p>
                    <small>Price: ₹${item.price}</small><br>
                    <small>Qty: ${item.quantity}</small>
                </div>
                <div class="d-flex align-items-center">
                    <span class="me-3">₹${(item.price * item.quantity).toFixed(2)}</span>
                    <button class="btn btn-sm btn-danger js-remove-item" data-index="${index}">Remove</button>
                </div>
            </div>
        `;
    });

    cartItemsContainer.innerHTML = itemsHTML || '<p>Your cart is empty!</p>';
    cartSubtotalElement.textContent = `₹${subtotal.toFixed(2)}`;
    cartItemsCountElement.textContent = totalItems;

  const desktopCartCountElement = document.querySelector('.js-cart-items-count.desktop');
    if (desktopCartCountElement) {
        desktopCartCountElement.textContent = totalItems;
    }

      const bagCartCountElement = document.querySelector('.js-cart-items-count.bag');
    if (bagCartCountElement) {
        bagCartCountElement.textContent = totalItems;
    }

    document.querySelectorAll('.js-remove-item').forEach(button => {
        button.addEventListener('click', function() {
            const itemIndex = this.getAttribute('data-index');
            removeFromCart(itemIndex);
        });
    });
}



    // Function to add item to the cart
    function addToCart(product) {
        const existingProduct = cart.find(item => item.productId === product.productId);

        if (existingProduct) {
            existingProduct.quantity += 1;
        } else {
            cart.push({
                productId: product.productId,
                productName: product.productName,
                price: product.price,
                quantity: 1
            });
        }

        $('.header-tools__cart').trigger('click');


        // Save the updated cart to sessionStorage
        sessionStorage.setItem('cart', JSON.stringify(cart));

        // Update the cart drawer
        updateCartDrawer();

        // Make the cart drawer visible
     if (typeof UomoSections.CartDrawer !== 'undefined') {
        const cartDrawer = new UomoSections.CartDrawer(); // Initialize cart drawer if not done already
        cartDrawer._initCartItemsList();  // You can manually call this if you want to update the items list on mobile devices
    }

    }



    // Function to remove an item from the cart
function removeFromCart(index) {
    // Remove item from the cart array
    cart.splice(index, 1);

    // Store the updated cart in sessionStorage
    sessionStorage.setItem('cart', JSON.stringify(cart));

    // Update the cart drawer UI
    updateCartDrawer();
}

    // Event listener for the Add to Cart button
    document.querySelectorAll('.booknow').forEach(button => {
        button.addEventListener('click', function(e) {
            e.preventDefault();

                // Update the cart drawer on page load
    updateCartDrawer();

            // Get product data from the button's data attributes
            const product = {
                productId: button.getAttribute('data-product-id'),
                productName: button.closest('.post').querySelector('h2').textContent.trim(),
                price: parseFloat(button.closest('.post').querySelector('.pricenum02 b').textContent.replace('₹', '').trim())
            };

            // Add to cart
            addToCart(product);
        });
    });

    // Initial update of the cart drawer on page load
    updateCartDrawer();
});




</script>

